import React from 'react'
import styled from 'styled-components'
import Navbar from '../components/Navbar';
import Announcement from '../components/Announcement';
import Newsletter from '../components/Newsletter';
import Footer from '../components/Footer';

const Container = styled.div``;

const Wrapper = styled.div`
    padding:50px;
    display:flex;
`;
const ImgContainer = styled.div`
    flex:1;
`;

const Image = styled.img`
    width : 100%
    height: 90vh;
    object-fit: cover;

`;

const InfoContainer = styled.div`
    flex:1;
    padding: 0px 50px;
`;

const Title = styled.h1`
    font-weight : 200;
`;

const Describtion = styled.p`
    margin : 20px 0px;
`;

const Price = styled.span`
    font-weight:100 ;
    font-size:40px;
    
`;

const Product = () => {
  return (
    <Container>
        <Navbar/>
        <Announcement/>
        <Wrapper>
            <ImgContainer>
                <Image src="/imgs/ac.jpg" />
            </ImgContainer>
            <InfoContainer>
                <Title>Al Ahly Red T-Shirt</Title>
                <Describtion>Lorem ipsum dolor sit amet consectetur adipisicing elit.
                     Fugit laudantium necessitatibus dolorum voluptates iusto natus vitae voluptatum commodi aliquid,
                     culpa ratione quia, adipisci repudiandae accusamus placeat, expedita deserunt et obcaecati?
                </Describtion>
                <Price>$ 20</Price>
                <FilterContainer>
                    <Filter>
                        <FilterTitle>Color</FilterTitle>
                        <FilterColor color="black" />
                        <FilterColor color="darkblue" />
                        <FilterColor color="gray" />
                    </Filter>

                    <Filter>
                        <FilterTitle>Size</FilterTitle>
                        <FilterSize>
                            <FilterSizeOption>XS</FilterSizeOption>
                            <FilterSizeOption>S</FilterSizeOption>
                            <FilterSizeOption>M</FilterSizeOption>
                            <FilterSizeOption>L</FilterSizeOption>
                            <FilterSizeOption>XL</FilterSizeOption>
                        </FilterSize>
                    </Filter>
                </FilterContainer>
            </InfoContainer>
        </Wrapper>
        <Newsletter/>
        <Footer/>
    </Container>
  )
}

export default Product
