import React from 'react'
import styled from 'styled-components'
import Navbar from '../components/Navbar';
import Announcement from '../components/Announcement';
import Newsletter from '../components/Newsletter';
import Footer from '../components/Footer';

const Container = styled.div``;
// Contain IMG & INFO
const Wrapper = styled.div`
    padding:50px;
    display:flex;
`;
///Element of Img Container
const ImgContainer = styled.div`
    flex:1;
`;
////1
const Image = styled.img`
    width : 100%
    height: 90vh;
    object-fit: cover;

`;
///Element of info Container

const InfoContainer = styled.div`
    flex:1;
    padding: 0px 50px;
`;
////1
const Title = styled.h1`
    font-weight : 200;
`;
////2
const Describtion = styled.p`
    margin : 20px 0px;
`;
////3
const Price = styled.span`
    font-weight:100 ;
    font-size:40px;
    
`;
///Element of Filter Container

const FilterContainer = styled.div`
    width :50%;
    margin: 30px 0px;
    display: flex;
    justify-content: space-between;
`;

const Filter = styled.div`
    dispaly: flex;
    align-items: center;
`;

const FilterTitle = styled.span`
    font-size: 20px;
    font-weight:200; 
`;

const FilterColor = styled.div`
    width : 20px;
    height: 20px;
    border-radius: 50%;
    background-color: #${props=>props.color};
`;

const FilterSize = styled.select``;

const FilterSizeOption = styled.option``;


const Product = () => {
  return (
    <Container>
        <Navbar/>
        <Announcement/>
        <Wrapper>
            <ImgContainer>
                <Image src="/imgs/ac.jpg" />
            </ImgContainer>
            <InfoContainer>
                <Title>Al Ahly Red T-Shirt</Title>
                <Describtion>Lorem ipsum dolor sit amet consectetur adipisicing elit.
                     Fugit laudantium necessitatibus dolorum voluptates iusto natus vitae voluptatum commodi aliquid,
                     culpa ratione quia, adipisci repudiandae accusamus placeat, expedita deserunt et obcaecati?
                </Describtion>
                <Price>$ 20</Price>
                <FilterContainer>
                    <Filter>
                        <FilterTitle>Color</FilterTitle>
                        <FilterColor color="black" />
                        <FilterColor color="darkblue" />
                        <FilterColor color="gray" />
                    </Filter>

                    <Filter>
                        <FilterTitle>Size</FilterTitle>
                        <FilterSize>
                            <FilterSizeOption>XS</FilterSizeOption>
                            <FilterSizeOption>S</FilterSizeOption>
                            <FilterSizeOption>M</FilterSizeOption>
                            <FilterSizeOption>L</FilterSizeOption>
                            <FilterSizeOption>XL</FilterSizeOption>
                        </FilterSize>
                    </Filter>
                </FilterContainer>
            </InfoContainer>
        </Wrapper>
        <Newsletter/>
        <Footer/>
    </Container>
  )
}

export default Product
