/************************************/
/*******Created by : AliSwilam*******/
/************************************/
import { Badge } from '@material-ui/core'; // To Get Logo of Badge "Number "
import { Search, ShoppingCartOutlined } from '@material-ui/icons'; // to Get Logo of Search & Cart
import React from 'react'
import styled from 'styled-components'
import {mobile} from '../responsive'


const Container = styled.div`
    height:60px ;
    ${mobile({ height: "50px" })}
`;
const Wrapper = styled.div`
    padding: 10px 20px;
    display: flex;
    align-item: center;
    justify-content: space-between;
`;
// flex :1 to make the width equal between them "it divide them by presantage"
// Component of Left side
const Left = styled.div`
    flex :1 ;
    display : flex; 
    align-item: center;
`;

const Language = styled.span`
    font-size: 14px;
    cursor: pointer;
`;

const SearchContainer = styled.div`
  border: 0.5px solid lightgray;
  display: flex;
  align-items: center;
  margin-left: 25px;
  padding: 5px;
`;

const Input = styled.input`
    border : none ;
`;
// Component of Center side
const Center = styled.div`
    flex :1 ;
    text-align: center; 
`;

const Logo = styled.h1`
    font-weight: bold;
`;
// Component of Center side
const Right = styled.div`
    flex :1 ; 
    display : flex ;
    align-item: center;
    justify-content:flex-end;


`;
    /// MenuBar Component
const MenuItem = styled.div`
  font-size: 14px;
  cursor: pointer;
  margin-left: 25px;
  `;
const Navbar = () => {
  return (
    <Container>
        <Wrapper>

        <Left>
            <Language>EN</Language>

                <SearchContainer>
                    <Input/>
                
                    <Search style={{color: "grey", fontSize:16}}/>
                </SearchContainer>

        </Left>


        <Center><Logo>AliBabaExpress.</Logo></Center>


        <Right>
            <MenuItem>REGISTER</MenuItem> 

            <MenuItem>SIGN IN</MenuItem>

            <MenuItem>
            <Badge badgeContent={4} color="primary"> 
                <ShoppingCartOutlined />
             </Badge>

            </MenuItem>
        </Right>

        </Wrapper> 
    </Container>
  )
}

export default Navbar
