import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons";
import styled from "styled-components"

const Container = styled.div`
    width: 100%;
    height: 100vh;
    display: flex;
    position: relative;
`;

const Arrow = styled.div`
    width: 50px;
    height: 50px;
    background-color: #fff7f7;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    position: absolute;
    top: 0;
    bottom: 0;
    left: ${(props) => props.direction === "left" && "10px"};
    right: ${(props) => props.direction === "right" && "10px"}; 
    margin: auto;
    cursor: pointer;
    opacity: 0.5;
  `;
// الغلاف ..
const Wrapper = styled.div`
    height:100%;
`;
///Component of Wrapper
////First : Slide that appear in middle
const Slide = styled.div`
    width:100vw;
    height:100vh;
    display: flex ;
    align-items: center;
`;
////Second : Img Container
const ImgContainer = styled.div`
    height:100%;
    flex :1;
`;
///// Imgs itself
const Image = styled.img`
    height:80%;
`;
////Third : Info of each Img Container
const InfoContainer = styled.div`
    padding:50px;
    flex :1;
`;

const Title = styled.h1`
    font-size: 70px;
`;

const Desc = styled.p`
    margin: 50px 0px;
    font-size: 20px;
    font-weight: 500;
    letter-spacing: 3px;
`;

const Button = styled.button`
    padding: 10px;
    font-size: 20px;
    background-color: transparent;
    cursor: pointer;
`;


const Slider = () => {
  return (
    <Container>
      <Arrow direction="left">
        <ArrowLeftOutlined/>
      </Arrow>
      <Wrapper>

          <Slide>
          <ImgContainer>
              <Image src="" />
          </ImgContainer>

          <InfoContainer>
            <Title></Title>
            <Describtion></Describtion>
            <Button></Button>
          </InfoContainer>

          </Slide>

      </Wrapper>

      <Arrow direction="right">
        <ArrowRightOutlined/>
      </Arrow>

    </Container>
  )
}

export default Slider
